<?phpuse GDO\Audio\GDO_Musician;
use GDO\Audio\GDT_SongMusicianInstrument;
use GDO\UI\GDT_Card;use GDO\UI\GDT_Label;use GDO\UI\GDT_EditButton;use GDO\UI\GDT_Divider;use GDO\Audio\GDT_MusicianSubtitle;/** @var $gdo GDO_Musician **/
$card = GDT_Card::make()->gdo($gdo);$card->title($gdo->gdoColumn('musician_name'));$card->avatar($gdo->gdoColumn('musician_photo')->withFileInfo(false));$card->subtitle(GDT_MusicianSubtitle::make()->musician($gdo));$card->addField($gdo->gdoColumn('musician_gender'));$card->addField($gdo->gdoColumn('musician_country'));$card->addField($gdo->gdoColumn('musician_songs'));$card->addField(GDT_Divider::make('div1'));$card->addField(GDT_Label::make('song'));$card->addField(GDT_Label::make('instrument'));$card->addField(GDT_Divider::make('div2'));$songs = $gdo->getSongs();foreach ($songs as $song){    $card->addField(GDT_SongMusicianInstrument::make()->song($song));}$card->editorFooter();if ($gdo->canEdit()){    $card->actions()->addField(GDT_EditButton::make('btn_edit')->href($gdo->hrefEdit()));}echo $card->render();