<?phpuse GDO\Audio\GDO_Musician;
use GDO\UI\GDT_Card;use GDO\UI\GDT_Label;use GDO\UI\GDT_EditButton;/** @var $gdo GDO_Musician **/
$card = GDT_Card::make()->gdo($gdo);$card->title($gdo->gdoColumn('musician_name'));$card->avatar($gdo->gdoColumn('musician_photo')->withFileInfo(false));$card->subtitle(GDT_Label::make()->label('musician_born', [$gdo->displayBirthdate(), $gdo->displayAge()]));$card->addField($gdo->gdoColumn('musician_gender'));$card->addField($gdo->gdoColumn('musician_country'));$card->addField($gdo->gdoColumn('musician_instrument'));$card->addField($gdo->gdoColumn('musician_songs'));$card->editorFooter();if ($gdo->canEdit()){    $card->actions()->addField(GDT_EditButton::make('btn_edit')->href($gdo->hrefEdit()));}echo $card->render();