<?phpuse GDO\Audio\GDO_Album;
use GDO\UI\GDT_ListItem;use GDO\Audio\GDT_AlbumSubtitle;use GDO\UI\GDT_Link;use GDO\File\GDT_ImageFile;/** @var $gdo GDO_Album **/$list = GDT_ListItem::make('album-'.$gdo->getID());if ($gdo->isFeatured()){    $list->addClass('featured');}/** @var $cover GDT_ImageFile **/$cover = $gdo->gdoColumnCopy('album_cover');$list->avatar($cover->previewHREF(href('Audio', 'Cover', '&file='))->variant('thumb')->imageSize(48, 48)->withFileInfo(false));$list->title(GDT_Link::make()->labelRaw($gdo->getVar('album_title'))->href($gdo->hrefShow()));$list->subtitle(GDT_AlbumSubtitle::make()->album($gdo)->band($gdo->getBand()));echo $list->render();